CREATE TABLE IF NOT EXISTS `item_type` (
	`id` int(11)  AUTO_INCREMENT,
	`description` text,
	PRIMARY KEY(`id`)
);
CREATE TABLE IF NOT EXISTS `item` (
	`id` int(11) AUTO_INCREMENT,
	`title` text,
	`content` text,
	`fk_item_type` int(11),
	FOREIGN KEY(`fk_item_type`) REFERENCES `item_type`(`id`),
	PRIMARY KEY(`id`)
);
CREATE TABLE IF NOT EXISTS `menu_item` (
	`id` int(11) AUTO_INCREMENT,
	`fk_item` int(11),
	`fk_parent` int(11),
	`norder` int(11),
	FOREIGN KEY(`fk_item`) REFERENCES `item`(`id`),
	FOREIGN KEY(`fk_parent`) REFERENCES `menu_item`(`id`) ON DELETE CASCADE,
	PRIMARY KEY(`id`)
);
