INSERT INTO item VALUES(25,'Fresubin[--{`type`: `sup`, `content`:`®`}--] 2 kcal Crema','[{`tab_content`:[{`data`:{`content`:[`Fresubin_2_kcal_Crema_cappuccino`,`Fresubin_2_kcal_Crema_fragola`]},`type`:`description_image`},{`data`:{`content`:`Fresubin [--{\`type\`: \`sup\`, \`content\`:\`®\`}--]2 kcal Crema è un alimento addensato orale nutrizionalmente completo, ipercalorico (2,0 kcal/g) e iperproteico (20% energia), senza fibre1. A ridotto contenuto di lattosio, senza glutine.`,`title`:`Descrizione`},`type`:`description_text`},{`data`:{`content`:`Fresubin [--{\`type\`: \`sup\`, \`content\`:\`®\`}--]2 kcal Crema è indicato per la gestione della nutrizione dei pazienti con o a rischio di malnutrizione patologia-correlata, in particolare in quelli con aumentata necessità di energia e proteine, restrizione dei fluidi e/o disfagia.`,`title`:`Indicazioni`},`type`:`description_text`},{`data`:{`content`:`Deve essere determinata dal medico in accordo al fabbisogno del paziente. Raccomandato per una nutrizione supplementare di 3-4 vasetti (750-1000 kcal)/die o per una nutrizione completa di 7-8 vasetti (1750-2000 kcal)/die.`,`title`:`Dose`},`type`:`description_text`},{`data`:{`content`:`Deve essere utilizzato sotto supervisione medica. Adatto come unica fonte di nutrizione. Non idoneo in bambini al di sotto di 3 anni. Da utilizzarsi con cautela nei bambini <6 anni. Da non utilizzare nei pazienti con galattosemia. Assicurare un adeguato apporto di liquidi.`,`title`:`Avvertenze importanti`},`type`:`description_text`},{`data`:{`content`:`Conservare a temperatura ambiente. I vasetti aperti possono essere conservati in frigorifero fino a 24 ore. Mescolare delicatamente prima dell’uso. Preparato in ambiente protetto.`,`title`:`Istruzioni per l’uso`},`type`:`description_text`},{`data`:{`content`:`Non indicato quando la nutrizione enterale non è praticabile, come in caso di sanguinamento gastrointestinale acuto, ileo e shock. Usare con cautela in caso di grave insufficienza d’organo con alterazioni del metabolismo e forme gravi di malassorbimento. Non adatto a pazienti con incapacità congenita di metabolizzare i nutrienti contenuti in Fresubin 2 kcal Crema.`,`title`:`Controindicazioni `},`type`:`description_text`},{`data`:{`content`:`7021701 Fresubin [--{\`type\`: \`sup\`, \`content\`:\`®\`}--]2 kcal Crema gusto fragola 125 g (cartone da 24 vasetti da 125 g) 7022701 Fresubin [--{\`type\`: \`sup\`, \`content\`:\`®\`}--]2 kcal Crema gusto cappuccino 125 g (cartone da 24 vasetti da 125 g)`,`title`:`Codici disponibili e confezionamento`},`type`:`description_text`}],`tab_title`:`Descrizione`},{`tab_content`:[{`data`:[{`desc`:`Valori Nutrizionali per 100 ml`,`type`:`title`},{`desc`:`Densità calorica`,`type`:`row`,`value`:`2 kcal/ml`},{`desc`:`Acqua`,`type`:`row`,`value`:`58 ml`},{`desc`:`Osmolarità`,`type`:`row`,`value`:`n.a.`},{`desc`:`Viscosità`,`type`:`row`,`value`:`21770 - 40430 mPa s`},{`desc`:`Proteine `,`type`:`subtitle`,`value`:`10,0 g`},{`desc`:`Lipidi`,`type`:`subtitle`,`value`:`7,80 g`},{`desc`:`MCT`,`type`:`tab1`,`value`:`0 g`},{`desc`:`LCT`,`type`:`tab1`,`value`:``},{`desc`:`di cui acidi grassi saturi`,`type`:`tab2`,`value`:`0,55 g`},{`desc`:`di cui acidi grassi monoinsaturi`,`type`:`tab2`,`value`:`5,84 g`},{`desc`:`di cui acidi grassi polinsaturi`,`type`:`tab2`,`value`:`1,40 g`},{`desc`:`di cui EPA e DHA`,`type`:`tab2`,`value`:`0 g`},{`desc`:`ω6/ω3`,`type`:`tab2`,`value`:`4,4: 1`},{`desc`:`Colesterolo `,`type`:`tab1`,`value`:`≤ 20 mg`},{`desc`:`Glucidi `,`type`:`subtitle`,`value`:`22,50 g`},{`desc`:`Zuccheri`,`type`:`tab1`,`value`:`11,0[--{\`type\`: \`sup\`, \`content\`:\`a\`}--] -13,40[--{\`type\`: \`sup\`, \`content\`:\`b\`}--]`},{`desc`:`di cui lattosio`,`type`:`tab2`,`value`:`≤ 0,50 g`},{`desc`:`di cui fruttosio`,`type`:`tab2`,`value`:`0 g`},{`desc`:`Fibre`,`type`:`subtitle`,`value`:`0 g`},{`desc`:`Vitamine`,`type`:`title`},{`desc`:`Vitamina A`,`type`:`row`,`value`:`213 mcg RE`},{`desc`:`di cui β-carotene`,`type`:`tab1`,`value`:`62